import React from "react";
import { useState, useMemo, useRef } from "react";
import "./App.css";
 import { v4 as uuidv4 } from "uuid";
import Task from "./compon/Task";
import { CSSTransition, TransitionGroup } from "react-transition-group";

function App() {
const [anim, setAnim]  = useState(true);
  const bodyInput = useRef('');
  const [newPost, setNewPost] = useState({});
  const [value, setValue] = useState("Shop");
  const [posts, setPosts] = useState([
    {
      id: uuidv4(),
      listName: "Tech",
      description: "Learn React.js",
    },
    {
      id: uuidv4(),
      listName: "Tech",
      description: "Learn Redux",
    },
    {
      id:uuidv4(),
      listName: "Shop",
      description: "Buy Sugar",
    }, 
    {
      id:uuidv4(),
      listName: "Shop",
      description: "Buy Milk",
    },
    {
      id: uuidv4(),
      listName: "Rest",
      description: "Go Picnic",
    },
  ]);
  function remove( props) {
    setAnim(true)
    console.log("remove function");  
    const afterFilter =  posts.filter(val => val.id !== props.postId);
    console.log(afterFilter);
    setPosts(afterFilter);
    setAnim(false)
  }
  function Add() {
    setAnim(true)
    setNewPost({});
    setPosts([
      ...posts,
      { ...newPost, id: uuidv4(), listName: value, description: bodyInput.current.value},
    ]);
    setAnim(false)
  }

  useMemo(() => {
  return {...posts};
  }, [posts]);
  return (
    <div className="App">
     Tickets
     <div>
        {posts.map((item) => (
          <CSSTransition classNames="item"   timeout={200} in={anim}   onEnter={() => setAnim(false)}
          onExit={() => setAnim(true)}>
          <Task
          key={item.id}
            remove={remove}
            listName={item.listName}
            postId={item.id}
            description={item.description}
          ></Task>
          </CSSTransition>
        ))}
      </div>
      <input
        ref={bodyInput}
        id="input"
        className="input"
        type="text"
        placeholder="New Task"
      ></input>
      <select className="select" onChange={(event) => setValue(event.target.value)}>
        <option value1="Shop">Shop</option>
        <option value1="Tech">Tech</option>
        <option value1="Rest">Rest</option>
      </select>
      <button onClick={Add} className="buttonAdd" type="text">&#x1F4AC;
     
      </button>
    </div>
  );
}
export default App;
