import React from "react";
import "./App.css";
import Login from "./compon/Login";
import Posts from "./compon/Posts";
import { BrowserRouter, Route,  Routes,} from "react-router-dom";
import{useState} from 'react'

function App() {
  const [token, setToken] = useState(false);
  const [password, setPassword]=useState();

  function LoginFunc(props){
     console.log(props.value);

     if(props.value === "test1234"){
     setToken(true);
  }
  else alert("wrong password");
  }
  if(!token) {
    return(
    <BrowserRouter>
    <Routes>
    <Route path="/" element={<Login className="input" onClick={LoginFunc} value={password} onChange={setPassword}/>}/>
    </Routes>
      </BrowserRouter>
    )
  }
else{
  return (
      <BrowserRouter>
       <Routes>
       <Route path ="/" element={<Posts/>} />
       </Routes>
      </BrowserRouter>

  );}
}
export default App;
