import React from "react";
import "./App.css";
import Login from "./compon/Login";
import Posts from "./compon/Posts";
import { BrowserRouter, Route,  Routes,} from "react-router-dom";
import{useState} from 'react'

function App() {
  const [token, setToken] = useState(false);
  const [password, setPassword]=useState('');

function updatePassword(props){

console.log(password)
setPassword(props.target.value)
}


  function LoginFunc(props,event){
     console.log(props);
     console.log(event)
     console.log(password)
     if(password==='test1234'){
     setToken(true);
  }
  else alert("wrong password");
  }
  if(!token) {
    return(
    <BrowserRouter>
    <Routes>
    <Route path="/" element={<Login className="input" value={password} onClick={LoginFunc} onChange={updatePassword}/>}/>
    </Routes>
      </BrowserRouter>
    )
  }
else{
  return (
      <BrowserRouter>
       <Routes>
       <Route path ="/" element={<Posts/>} />
       </Routes>
      </BrowserRouter>

  );}
}
export default App;
