import Login from "./compon/Login/Login";
import Posts from "./compon/Main/Posts";
import { useState, useReducer } from "react";
import MainContext from "./compon/Context/MainContext";
import ReducerContext from "./compon/Context/ReducerContext";
import { v4 as uuidv4 } from "uuid";
import Data from "./compon/Data/Data";

function App() {
  const [token, setToken] = useState(false);
  const [openSignUp, setOpenSignUp] = useState(false);
  const [state, dispatch] = useReducer(counterReducer, { posts: Data });

  function counterReducer(state, action) {
    switch (action.type) {
      case "ADD_POST":
        return {
          posts: [
            ...state.posts,
            {
              id: uuidv4(),
              image: action.payload.image,
              listName: action.payload.value,
              description: action.payload.valueInput,
            },
          ],
        };
      case "REMOVE_POST":
        return {
          posts: state.posts.filter((itm) => itm.id !== action.payload),
        };
      default:
        return console.log("default");
    }
  }
  return (
    <div>
      {token ? (
        <MainContext.Provider
          value={{
            setToken,
          }}
        >
          <ReducerContext.Provider
            value={{
              state,
              dispatch,
            }}
          >
            <Posts />
          </ReducerContext.Provider>
        </MainContext.Provider>
      ) : (
        <MainContext.Provider
          value={{
            setToken,
            openSignUp,
            setOpenSignUp,
          }}
        >
          <Login />
        </MainContext.Provider>
      )}
    </div>
  );
}
export default App;
